let initialState = {
  deckCard: {},
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case "FETCH_DECK_CARD":
      state.card.deckCard = {...deckCard, payload};
      return {...state};
    default:
      return state;
  }
};

export default reducer;
