import React, { useState } from "react";
import Game from "../Game";
import { useFormik } from "formik";
import { connect } from "react-redux";
import { createAction } from "../../redux/actions";
import * as yup from "yup";

const userSchema = yup.object().shape({
  username: yup.string().required("This field is required"),
  email: yup
    .string()
    .required("This field is required")
    .email("Email is invalid"),
  phone: yup
    .string()
    .required("This field is required")
    .matches(/^[0-9]+$/, "Your phone have to contain numbers"),
});

/*
TODOLIST
1. Xử lý form, lấy info người dùng nhập
2. dispatch action, push user into playerLisst,
3. Hiện màn hình game, ẩn form

CÁC THƯ VIỆN FORM: formik, redux-form,...
validation: yup, joi, validators,...
*/
const Home = (props) => {
  const [isStarted, setIsStarted] = useState(false);
  // const [form, setForm] = useState({
  // username: "",
  // email: "",
  // phone: "",
  // totalPoint: 25000,
  // cards: [],
  // });
  // const handleChange = (event) => {
  //   setForm({
  //     ...form,
  //     [event.target.name]: event.target.value,
  //   });
  // };
  const { handleChange, values, errors, isValid } = useFormik({
    initialValues: {
      username: "",
      email: "",
      phone: "",
      totalPoint: 25000,
      cards: [],
    },
    validationSchema: userSchema,
    validateOnMount: true,
  });
  const handleSubmit = (event) => {
    event.preventDefault();
    //check form invalid
    if (!isValid) return;

    console.log(values);
    props.dispatch(createAction(values));
  };
  return (
    <>
      {isStarted ? (
        <Game />
      ) : (
        <div
          className="text-center"
          style={{
            width: "100vw",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="diplay-4 mb-5"> Welcome to Pocker Center</h1>
          <h3>Fill your info and start</h3>
          <form className="w-25 mx-auto" onSubmit={handleSubmit}>
            <input
              type="input"
              placeholder="username"
              name="username"
              onChange={handleChange}
              className="w-100 form-control mb-3"
            />
            <p></p>
            <input
              type="input"
              placeholder="email"
              name="email"
              onChange={handleChange}
              className="w-100 form-control mb-3"
            />
            <input
              type="input"
              placeholder="phone"
              name="phone"
              onChange={handleChange}
              className="w-100 form-control mb-3"
            />
            <button className="btn btn-success">Start new Game</button>
          </form>
        </div>
      )}
    </>
  );
};

export default connect()(Home);
