import React from "react";
import { useSelector } from "react-redux";
import axios from 'axios';
const Control = () => {
  const player = useSelector((state) => state.player.playerList);
  const deckCard = useSelector((state)=>{
    state.card
  })
  const drawCards = () => {
   
    /*
    1. Call api lấy 12 lá bài
    2. dispatch action to player reducer , draw 3 card per each player
    */
  }
  return (
    <div className="d-flex  justify-content-end container">
      <div className="border d-flex justify-content-center align-items-center px-2">
        <button className="btn btn-success mr-2">Shuffle</button>
        <button onClick = {drawCards} className="btn btn-info mr-2">Draw</button>
        <button className="btn btn-primary mr-2">Reveal</button>
      </div>
      <div className="d-flex">
        {player.map((player) => (
          <div className="border px-3 text-center" key ={player.code}>
            <p>{player.username}</p>
            <p className = "text-danger lead">{player.totalPoint.toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Control;
