import {makeStyles} from "@material-ui/core";
const useStyle = makeStyles ((theme)=> {
    return {
        tittle: {
            backgroundColor: "red",
            //reponsive trực tiếp
            //theme nếu có viết thì nó sẽ lấy cái của mình, còn ko sẽ lấy cái mặc định
            [theme.breakpoints.down("lg")]: {
                backgroundColor: "green",
            },

        }
    }
})
